import Foundation

struct FallbackData {
    static let pi = ""
    
    static let e = ""
    
    static let phi = ""
    
    static let sqrt2 = ""
    
    static func digits(for constant: Constant) -> [UInt8] {
        let str: String
        switch constant {
        case .pi: str = pi
        case .e: str = e
        case .phi: str = phi
        case .sqrt2: str = sqrt2
        }
        return Array(str.utf8).map { $0 - 48 }
    }
}
